import React from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';

const Navbar: React.FC = () => {
  const { user, logout } = useAuth();
  const navigate = useNavigate();

  const handleLogout = () => {
    logout();
    navigate('/');
  };

  return (
    <nav className="navbar">
      <div className="navbar-content">
        <Link to="/" className="logo">
          üèàüèÄ ROCK SPORTS
        </Link>
        
        <div className="nav-links">
          {user ? (
            <>
              <Link to="/dashboard" className="nav-link">üè† Dashboard</Link>
              {user.role !== 'admin' && (
                <>
                  <Link to="/sports" className="nav-link">üèàüèÄ Live Sports</Link>
                  <Link to="/basketball" className="nav-link">üèÄ NBA</Link>
                  <Link to="/betting" className="nav-link">üéØ Place Bet</Link>
                  <Link to="/history" className="nav-link">üìä History</Link>
                </>
              )}
              {user.role === 'admin' && (
                <Link to="/admin" className="nav-link">üè¶ Admin</Link>
              )}
              <div className="credits">
                {user.role === 'admin' ? 'üëë Admin' : `üí∞ $${user.credits.toFixed(2)}`}
              </div>
              <button onClick={handleLogout} className="btn btn-danger" style={{ padding: '10px 20px', fontSize: '14px' }}>
                üö™ Logout
              </button>
            </>
          ) : (
            <>
              <Link to="/login" className="nav-link">üîë Login</Link>
              <Link to="/register" className="nav-link">üöÄ Register</Link>
            </>
          )}
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
