import React, { useState, useEffect } from 'react';
import { useAuth } from '../contexts/AuthContext';
import axios from 'axios';

interface Match {
  id: string;
  sport: string;
  homeTeam: string;
  awayTeam: string;
  commenceTime: string;
  commenceTimeFormatted: string;
  odds: {
    h2h: Array<{
      team: string;
      price: number;
      point?: number;
    }>;
    spreads: Array<{
      team: string;
      price: number;
      point?: number;
    }>;
    totals: Array<{
      team: string;
      price: number;
      point?: number;
    }>;
  };
}

interface Selection {
  id: string;
  matchId: string;
  sport: string;
  homeTeam: string;
  awayTeam: string;
  market: string;
  team: string;
  odds: number;
  point?: number;
  selected: boolean;
}

const Betting: React.FC = () => {
  const { user, updateUser } = useAuth();
  const [matches, setMatches] = useState<Match[]>([]);
  const [selections, setSelections] = useState<Selection[]>([]);
  const [stake, setStake] = useState<number>(10);
  const [message, setMessage] = useState<{ type: 'success' | 'error' | 'info', text: string } | null>(null);
  const [loading, setLoading] = useState(false);
  const [selectedMarket, setSelectedMarket] = useState<'h2h' | 'spreads' | 'totals'>('h2h');
  const [selectedSport, setSelectedSport] = useState<'all' | 'NBA' | 'NFL'>('all');

  const selectedSelections = selections.filter(s => s.selected);
  const totalOdds = selectedSelections.reduce((acc, selection) => acc * (selection.odds / 100 + 1), 1);
  const potentialWin = stake * (totalOdds - 1);

  useEffect(() => {
    fetchMatches();
  }, [selectedSport]);

  const fetchMatches = async () => {
    try {
      let endpoint = '/api/sports/upcoming';
      
      if (selectedSport === 'NBA') {
        endpoint = '/api/basketball/upcoming';
      } else if (selectedSport === 'NFL') {
        endpoint = '/api/football/upcoming';
      }
      
      const response = await axios.get(endpoint);
      setMatches(response.data);
    } catch (error) {
      console.error('Error fetching matches:', error);
      setMessage({ type: 'error', text: 'Failed to load sports matches' });
    }
  };

  const toggleSelection = (matchId: string, sport: string, market: string, team: string, odds: number, point?: number) => {
    const selectionId = `${matchId}-${market}-${team}`;
    const match = matches.find(m => m.id === matchId);
    
    if (!match) return;

    setSelections(prev => {
      const existingIndex = prev.findIndex(s => s.id === selectionId);
      
      if (existingIndex >= 0) {
        // Remove selection
        return prev.filter(s => s.id !== selectionId);
      } else {
        // Add selection
        const newSelection: Selection = {
          id: selectionId,
          matchId,
          sport,
          homeTeam: match.homeTeam,
          awayTeam: match.awayTeam,
          market,
          team,
          odds,
          point,
          selected: true
        };
        return [...prev, newSelection];
      }
    });
  };

  const formatOdds = (price: number) => {
    if (price > 0) return `+${price}`;
    return price.toString();
  };

  const getMarketLabel = (market: string) => {
    switch (market) {
      case 'h2h': return 'Moneyline';
      case 'spreads': return 'Spread';
      case 'totals': return 'Total';
      default: return market;
    }
  };

  const getTeamLabel = (team: string, market: string, point?: number) => {
    switch (market) {
      case 'h2h': return team;
      case 'spreads': return `${team} ${point && point > 0 ? '+' : ''}${point || 0}`;
      case 'totals': return `${point && point > 0 ? 'Over' : 'Under'} ${Math.abs(point || 0)}`;
      default: return team;
    }
  };

  const getSportIcon = (sport: string) => {
    switch (sport) {
      case 'NBA': return 'üèÄ';
      case 'NFL': return 'üèà';
      default: return '‚öΩ';
    }
  };

  const placeBet = async () => {
    if (selectedSelections.length < 2) {
      setMessage({ type: 'error', text: 'Please select at least 2 options for a parlay bet' });
      return;
    }

    if (stake < 1) {
      setMessage({ type: 'error', text: 'Minimum stake is $1' });
      return;
    }

    if (stake > (user?.credits || 0)) {
      setMessage({ type: 'error', text: 'Insufficient credits' });
      return;
    }

    setLoading(true);
    setMessage(null);

    try {
      const response = await axios.post('/api/bet/parlay', {
        selections: selectedSelections,
        stake: stake
      });

      setMessage({ 
        type: 'success', 
        text: `Bet placed successfully! Potential win: $${potentialWin.toFixed(2)}` 
      });

      // Update user credits
      if (user) {
        updateUser({ credits: user.credits - stake });
      }

      // Reset form
      setSelections([]);
      setStake(10);

    } catch (error: any) {
      setMessage({ 
        type: 'error', 
        text: error.response?.data?.message || 'Failed to place bet' 
      });
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      <div className="card" style={{ marginBottom: '30px' }}>
        <h2 style={{ color: '#333', marginBottom: '20px' }}>Place a Parlay Bet</h2>
        <p style={{ color: '#6c757d', marginBottom: '20px' }}>
          Select at least 2 options to create a parlay bet. The more selections you add, the higher your potential winnings!
        </p>
        
        <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '20px' }}>
          <span style={{ fontWeight: 'bold' }}>Available Credits: ${user?.credits.toFixed(2)}</span>
          <span style={{ fontWeight: 'bold', color: '#667eea' }}>
            {selectedSelections.length} selection{selectedSelections.length !== 1 ? 's' : ''} selected
          </span>
        </div>
      </div>

      {message && (
        <div className={`alert alert-${message.type === 'error' ? 'error' : message.type === 'success' ? 'success' : 'info'}`}>
          {message.text}
        </div>
      )}

      <div className="grid grid-2">
        <div className="card">
          <h3 style={{ color: '#667eea', marginBottom: '20px' }}>Available Selections</h3>
          <div style={{ display: 'flex', flexDirection: 'column', gap: '12px' }}>
            {selections.map((selection) => (
              <div
                key={selection.id}
                className={`bet-selection ${selection.selected ? 'selected' : ''}`}
                onClick={() => toggleSelection(selection.id)}
                style={{
                  cursor: 'pointer',
                  backgroundColor: selection.selected ? '#e3f2fd' : '#f8f9fa',
                  border: selection.selected ? '2px solid #667eea' : '2px solid transparent',
                  transition: 'all 0.3s ease'
                }}
              >
                <span style={{ fontWeight: '500' }}>{selection.name}</span>
                <span className="odds">+{((selection.odds - 1) * 100).toFixed(0)}</span>
              </div>
            ))}
          </div>
        </div>

        <div className="card">
          <h3 style={{ color: '#667eea', marginBottom: '20px' }}>Bet Slip</h3>
          
          {selectedSelections.length === 0 ? (
            <div style={{ textAlign: 'center', color: '#6c757d', padding: '20px' }}>
              Select at least 2 options to place a bet
            </div>
          ) : (
            <>
              <div style={{ marginBottom: '20px' }}>
                <h4 style={{ marginBottom: '12px' }}>Selected Selections:</h4>
                {selectedSelections.map((selection) => (
                  <div key={selection.id} style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '8px' }}>
                    <span style={{ fontSize: '14px' }}>{selection.name}</span>
                    <span style={{ fontWeight: 'bold', color: '#28a745' }}>
                      +{((selection.odds - 1) * 100).toFixed(0)}
                    </span>
                  </div>
                ))}
              </div>

              <div style={{ marginBottom: '20px' }}>
                <label className="form-label">Stake Amount ($)</label>
                <input
                  type="number"
                  min="1"
                  max={user?.credits || 0}
                  value={stake}
                  onChange={(e) => setStake(Number(e.target.value))}
                  className="stake-input"
                  style={{ width: '100%' }}
                />
              </div>

              <div style={{ borderTop: '1px solid #e9ecef', paddingTop: '20px' }}>
                <div style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '8px' }}>
                  <span>Total Odds:</span>
                  <span style={{ fontWeight: 'bold' }}>{totalOdds.toFixed(2)}</span>
                </div>
                <div style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '16px' }}>
                  <span>Potential Win:</span>
                  <span className="potential-win">${potentialWin.toFixed(2)}</span>
                </div>
                
                <button
                  onClick={placeBet}
                  disabled={loading || selectedSelections.length < 2 || stake < 1}
                  className="btn btn-primary"
                  style={{ width: '100%' }}
                >
                  {loading ? 'Placing Bet...' : `Place Bet ($${stake.toFixed(2)})`}
                </button>
              </div>
            </>
          )}
        </div>
      </div>

      <div className="card" style={{ marginTop: '30px' }}>
        <h3 style={{ color: '#667eea', marginBottom: '20px' }}>How Parlay Betting Works</h3>
        <div className="grid grid-2">
          <div>
            <h4 style={{ color: '#333', marginBottom: '12px' }}>üéØ Selection</h4>
            <p style={{ color: '#6c757d', fontSize: '14px' }}>
              Choose 2 or more selections from the available options. Each selection has different odds.
            </p>
          </div>
          <div>
            <h4 style={{ color: '#333', marginBottom: '12px' }}>üí∞ Calculation</h4>
            <p style={{ color: '#6c757d', fontSize: '14px' }}>
              Your total odds are calculated by multiplying all individual odds together. Higher odds = bigger potential wins!
            </p>
          </div>
          <div>
            <h4 style={{ color: '#333', marginBottom: '12px' }}>üèÜ Winning</h4>
            <p style={{ color: '#6c757d', fontSize: '14px' }}>
              To win, ALL your selections must be correct. If even one selection loses, the entire bet loses.
            </p>
          </div>
          <div>
            <h4 style={{ color: '#333', marginBottom: '12px' }}>üí° Strategy</h4>
            <p style={{ color: '#6c757d', fontSize: '14px' }}>
              More selections = higher potential winnings, but also higher risk. Choose wisely!
            </p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Betting;
