import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import axios from 'axios';

interface Bet {
  id: number;
  bet_type: string;
  selections: any[];
  odds: number;
  stake: number;
  potential_win: number;
  status: string;
  created_at: string;
}

const Dashboard: React.FC = () => {
  const { user, updateUser } = useAuth();
  const [recentBets, setRecentBets] = useState<Bet[]>([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchRecentBets();
    fetchUserProfile();
  }, []);

  const fetchUserProfile = async () => {
    try {
      const response = await axios.get('/api/profile');
      updateUser(response.data);
    } catch (error) {
      console.error('Error fetching user profile:', error);
    }
  };

  const fetchRecentBets = async () => {
    try {
      const response = await axios.get('/api/bets/history');
      setRecentBets(response.data.slice(0, 5)); // Get last 5 bets
    } catch (error) {
      console.error('Error fetching recent bets:', error);
    } finally {
      setLoading(false);
    }
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'won': return '#28a745';
      case 'lost': return '#dc3545';
      case 'pending': return '#ffc107';
      default: return '#6c757d';
    }
  };

  const getStatusText = (status: string) => {
    switch (status) {
      case 'won': return 'Won';
      case 'lost': return 'Lost';
      case 'pending': return 'Pending';
      default: return 'Unknown';
    }
  };

  return (
    <div>
      <div className="card" style={{ background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)', color: 'white', marginBottom: '30px' }}>
        <h1 style={{ fontSize: '32px', marginBottom: '10px' }}>
          Welcome back, {user?.username}!
        </h1>
        <p style={{ fontSize: '18px', opacity: 0.9 }}>
          Ready to place some bets? You have ${user?.credits.toFixed(2)} in credits.
        </p>
      </div>

      <div className="grid grid-2">
        <div className="card">
          <h3 style={{ color: '#667eea', marginBottom: '20px' }}>Quick Actions</h3>
          <div style={{ display: 'flex', flexDirection: 'column', gap: '12px' }}>
            <Link to="/basketball" className="btn btn-primary" style={{ textAlign: 'center' }}>
              üèÄ NBA Matches
            </Link>
            <Link to="/betting" className="btn btn-secondary" style={{ textAlign: 'center' }}>
              üéØ Place a Bet
            </Link>
            <Link to="/history" className="btn btn-secondary" style={{ textAlign: 'center' }}>
              üìä View History
            </Link>
          </div>
        </div>

        <div className="card">
          <h3 style={{ color: '#667eea', marginBottom: '20px' }}>Account Summary</h3>
          <div style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '10px' }}>
            <span>Available Credits:</span>
            <span style={{ fontWeight: 'bold', color: '#28a745' }}>
              ${user?.credits.toFixed(2)}
            </span>
          </div>
          <div style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '10px' }}>
            <span>Total Bets:</span>
            <span style={{ fontWeight: 'bold' }}>{recentBets.length}</span>
          </div>
          <div style={{ display: 'flex', justifyContent: 'space-between' }}>
            <span>Member Since:</span>
            <span style={{ fontWeight: 'bold' }}>
              {user ? new Date().toLocaleDateString() : 'N/A'}
            </span>
          </div>
        </div>
      </div>

      <div className="card">
        <h3 style={{ color: '#667eea', marginBottom: '20px' }}>Recent Bets</h3>
        {loading ? (
          <div style={{ textAlign: 'center', padding: '20px' }}>
            Loading recent bets...
          </div>
        ) : recentBets.length === 0 ? (
          <div style={{ textAlign: 'center', padding: '20px', color: '#6c757d' }}>
            No bets yet. <Link to="/betting" style={{ color: '#667eea' }}>Place your first bet!</Link>
          </div>
        ) : (
          <div>
            {recentBets.map((bet) => (
              <div key={bet.id} className="bet-card">
                <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '10px' }}>
                  <span style={{ fontWeight: 'bold' }}>
                    {bet.bet_type.charAt(0).toUpperCase() + bet.bet_type.slice(1)} Bet
                  </span>
                  <span 
                    style={{ 
                      padding: '4px 8px', 
                      borderRadius: '4px', 
                      backgroundColor: getStatusColor(bet.status),
                      color: 'white',
                      fontSize: '12px',
                      fontWeight: 'bold'
                    }}
                  >
                    {getStatusText(bet.status)}
                  </span>
                </div>
                <div style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '8px' }}>
                  <span>Stake:</span>
                  <span>${bet.stake.toFixed(2)}</span>
                </div>
                <div style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '8px' }}>
                  <span>Potential Win:</span>
                  <span style={{ color: '#28a745', fontWeight: 'bold' }}>
                    ${bet.potential_win.toFixed(2)}
                  </span>
                </div>
                <div style={{ display: 'flex', justifyContent: 'space-between', fontSize: '12px', color: '#6c757d' }}>
                  <span>{bet.selections.length} selections</span>
                  <span>{new Date(bet.created_at).toLocaleDateString()}</span>
                </div>
              </div>
            ))}
            <div style={{ textAlign: 'center', marginTop: '20px' }}>
              <Link to="/history" className="btn btn-secondary">
                View All Bets
              </Link>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default Dashboard;
